            Logout
          </button>
        </div>
      </div>
      <div className="FormBody">
        <div className="Form2">
          <h2 className="Heading">Exam</h2>
          {questions.map((dt, k) => {
            return (
              <div id={k} className="QuestionCard">
                <div className="QuestionDiv">
                  <h2 className="ques">
                    Question {k + 1}: {dt.Questions}
                  </h2>
                </div>
                <div className="RadioGroup" onChange={handleoptionchange}>
                  <div className="RadioBtn">
                    <input
                      name="choice1"
                      className="inputcheck"
                      type="radio"
                      value={"A"}
                    ></input>
                    <label className="checklabel" htmlFor="choice1">
                      A: {dt.A}
                    </label>
                  </div>
                  <div className="RadioBtn">
                    <input
                      name="choice1"
                      className="inputcheck"
                      type="radio"
                      value={"B"}

                    ></input>
                    <label className="checklabel" htmlFor="choice2">
                      B: {dt.B}
                    </label>
                  </div>
                  <div className="RadioBtn">
                    <input
                      name="choice1"
                      className="inputcheck"
                      type="radio"
                      value={"C"}

                    ></input>
                    <label className="checklabel" htmlFor="choice3">
                      C: {dt.C}
                    </label>
                  </div>
                  <div className="RadioBtn">
                    <input
                      name="choice1"
                      className="inputcheck"
                      type="radio"
                      value={"D"}

                    ></input>
                    <label className="checklabel" htmlFor="choice4">
                      D: {dt.D}
                    </label>
                  </div>
                </div>
                <div className="ButtonDiv">
                  <button
                    id="prev"
                    disabled={prev}
                    className="btn2"
                    key={"P" + k}
                    onClick={() => handleclickprev(k)}
                  >
                    Prev
                  </button>
                  <button
                    id="flag"
                    className="btn2"
                    key={"F" + k}
                    onClick={() => handleclickflag(k)}
                  >
                    Mark for Review
                  </button>
                  <button
                    id="next"
                    disabled={next}
                    className="btn2"
                    key={"N" + k}
                    onClick={() => handleclicknext(k)}
                  >
                    Next
                  </button>
                </div>
                <div className="ButtonDiv">
                  <button
                    id="subm"
                    className="btn3"
                    key={k}
                    onClick={() => handleclicksubmit(k)}
                  >
                    End Exam
                  </button>
                </div>
              </div>
            );
          })}
        </div>
        <div className="Form3">
          <Timer />
          <div className="qboxdiv">
            {questions.map((dt, k) => {
              return (
                <button id={"qbox" + k} key={"qbox" + k} onClick={() => handleclickqbox(k)} className="qbox">{k + 1}</button>
              )
            })}
          </div>
        </div>
      </div>
      <div className="Footer">
        <div className="FooterR1">
          <img
            src={require("../static/img/logo1.png")}
